import { Plane, Plus, Trash2, Edit2, X, MapPin, Calendar, TrendingUp, Award, Search, LogOut, User, Mail, Lock, Eye, EyeOff } from 'lucide-react';

// Comprehensive airport database (60+ major airports worldwide)
