  { code: 'CPT', name: 'Cape Town International Airport', city: 'Cape Town', country: 'South Africa', lat: -33.9715, lon: 18.6021 },
  { code: 'NBO', name: 'Jomo Kenyatta International Airport', city: 'Nairobi', country: 'Kenya', lat: -1.3192, lon: 36.9278 },
];

const serviceClasses = ['Economy', 'Premium Economy', 'Business', 'First'];

// Haversine formula for distance calculation
const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 3958.8;
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

// Airport search component
const AirportSearch = ({ value, onChange, onSelect, placeholder }) => {
  const [searchTerm, setSearchTerm] = useState(value || '');
  const [showResults, setShowResults] = useState(false);
  const [filteredAirports, setFilteredAirports] = useState([]);

  // Sync with external value changes
  useEffect(() => {
    setSearchTerm(value || '');
  }, [value]);

  useEffect(() => {
    if (searchTerm.length >= 2) {
      const results = AIRPORTS_DATABASE.filter(airport =>
        airport.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
        airport.city.toLowerCase().includes(searchTerm.toLowerCase()) ||
        airport.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        airport.country.toLowerCase().includes(searchTerm.toLowerCase())
      ).slice(0, 8);
      setFilteredAirports(results);
      setShowResults(true);
    } else {
      setShowResults(false);
    }
  }, [searchTerm]);

  const handleSelect = (airport) => {
    setSearchTerm(airport.code);
    onSelect(airport);
    setShowResults(false);
  };

  const handleInputChange = (e) => {
    const newValue = e.target.value;
    setSearchTerm(newValue);
    onChange(newValue);
  };

  return (
    <div className="relative">
      <input
        type="text"
        value={searchTerm}
        onChange={handleInputChange}
        onFocus={() => searchTerm.length >= 2 && setShowResults(true)}
        onBlur={() => setTimeout(() => setShowResults(false), 200)}
        placeholder={placeholder}
        className="input-field"
      />

      {showResults && filteredAirports.length > 0 && (
        <div className="search-results">
          {filteredAirports.map((airport) => (
            <button
              key={airport.code}
              type="button"
              onClick={() => handleSelect(airport)}
              className="search-result-item"
            >
              <div className="flex items-center justify-between gap-4">
                <div className="text-left">
                  <div className="result-code">{airport.code}</div>
                  <div className="result-city">{airport.city}, {airport.country}</div>
                </div>
                <div className="result-name">{airport.name}</div>
              </div>
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

const FlightTracker = () => {
  const [user, setUser] = useState(null);
  const [showAuth, setShowAuth] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [flights, setFlights] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingFlight, setEditingFlight] = useState(null);
  const [formData, setFormData] = useState({
    origin: null,
    destination: null,
    date: '',
    serviceClass: 'Economy',
  });
  const [authData, setAuthData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(true);

  // Load user and flights from storage
  useEffect(() => {
    const loadData = async () => {
      try {
        const userData = localStorage.getItem('user-profile');
        if (userData) {
          setUser(JSON.parse(userData));
          setShowAuth(false);
        } else {
          setShowAuth(true);
        }

        const flightsData = localStorage.getItem('flights-data');
        if (flightsData) {
          setFlights(JSON.parse(flightsData));
        }
      } catch (error) {
        console.log('No existing data');
        setShowAuth(true);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, []);

  const saveFlights = async (updatedFlights) => {
    try {
      localStorage.setItem('flights-data', JSON.stringify(updatedFlights));
    } catch (error) {
      console.error('Failed to save flights:', error);
    }
  };

  const handleAuth = async (e) => {
    e.preventDefault();

    if (authMode === 'register') {
      if (authData.password !== authData.confirmPassword) {
        alert('Passwords do not match');
        return;
      }

      const newUser = {
        id: Date.now(),
        email: authData.email,
        name: authData.name,
        createdAt: new Date().toISOString()
      };

      localStorage.setItem('user-profile', JSON.stringify(newUser));
      setUser(newUser);
      setShowAuth(false);
    } else {
      const userData = localStorage.getItem('user-profile');
      if (userData) {
        const existingUser = JSON.parse(userData);
        if (existingUser.email === authData.email) {
          setUser(existingUser);
          setShowAuth(false);
        } else {
          alert('User not found. Please register.');
        }
      } else {
        alert('No account found. Please register.');
      }
    }

    setAuthData({ email: '', password: '', confirmPassword: '', name: '' });
  };

  const handleLogout = async () => {
    setUser(null);
    setShowAuth(true);
  };

  const calculateFlightDetails = (origin, destination) => {
    if (!origin || !destination) return null;

    const distanceMiles = calculateDistance(
      origin.lat,
      origin.lon,
      destination.lat,
      destination.lon
    );

    return {
      distanceMiles: Math.round(distanceMiles),
      distanceKm: Math.round(distanceMiles * 1.60934),
      originAirport: origin,
      destAirport: destination,
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.origin || !formData.destination) {
      alert('Please select both origin and destination airports');
      return;
    }

    const flightDetails = calculateFlightDetails(formData.origin, formData.destination);
    if (!flightDetails) {
      alert('Unable to calculate flight distance');
      return;
    }

    const newFlight = {
      id: editingFlight ? editingFlight.id : Date.now(),
      origin: formData.origin.code,
      destination: formData.destination.code,
      date: formData.date,
      serviceClass: formData.serviceClass,
      ...flightDetails,
      timestamp: editingFlight ? editingFlight.timestamp : Date.now(),
    };

    const updatedFlights = editingFlight
      ? flights.map(f => f.id === editingFlight.id ? newFlight : f)
      : [...flights, newFlight];

    setFlights(updatedFlights);
    saveFlights(updatedFlights);

    setShowForm(false);
    setEditingFlight(null);
    setFormData({ origin: null, destination: null, date: '', serviceClass: 'Economy' });
  };

  const handleDelete = async (id) => {
    const updatedFlights = flights.filter(f => f.id !== id);
    setFlights(updatedFlights);
    saveFlights(updatedFlights);
  };

  const handleEdit = (flight) => {
    setEditingFlight(flight);
    setFormData({
      origin: flight.originAirport,
      destination: flight.destAirport,
      date: flight.date,
      serviceClass: flight.serviceClass,
    });
    setShowForm(true);
  };

  const totalMiles = flights.reduce((sum, flight) => sum + flight.distanceMiles, 0);
  const totalKm = flights.reduce((sum, flight) => sum + flight.distanceKm, 0);
  const totalFlights = flights.length;

