            {/* Flight List */}
            <div>
              <h2 className="section-title">Your flights</h2>

              {flights.length === 0 ? (
                <div className="empty-state">
                  <Plane className="empty-icon" size={64} />
                  <div className="empty-title">No flights yet</div>
                  <div className="empty-subtitle">Start tracking your journeys</div>
                  <button
                    onClick={() => setShowForm(true)}
                    className="primary-button"
                    style={{ margin: '0 auto' }}
                  >
                    <Plus size={18} />
                    Add your first flight
                  </button>
                </div>
              ) : (
                <div className="flight-list">
                  {flights.sort((a, b) => new Date(b.date) - new Date(a.date)).map((flight) => (
                    <div key={flight.id} className="flight-card">
                      <div className="flight-header">
                        <div>
                          <div className="airport-code">{flight.origin}</div>
                          <div className="airport-city">{flight.originAirport.city}</div>
                        </div>

                        <div className="flight-arrow"></div>

                        <div style={{ textAlign: 'right' }}>
                          <div className="airport-code">{flight.destination}</div>
                          <div className="airport-city">{flight.destAirport.city}</div>
                        </div>
                      </div>

                      <div className="flight-meta">
                        <span className="meta-badge">{flight.distanceMiles.toLocaleString()} mi</span>
                        <span className="meta-badge">{flight.distanceKm.toLocaleString()} km</span>
                        <span className="meta-badge">{flight.serviceClass}</span>
                      </div>

                      <div className="flight-date">
                        <Calendar size={14} />
                        {new Date(flight.date).toLocaleDateString('en-US', { 
                          year: 'numeric', 
                          month: 'long', 
                          day: 'numeric' 
                        })}
                      </div>

                      <div className="flight-actions">
                        <button onClick={() => handleEdit(flight)} className="action-button">
                          <Edit2 size={14} />
                          Edit
                        </button>
                        <button onClick={() => handleDelete(flight.id)} className="action-button">
                          <Trash2 size={14} />
                          Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </main>
        </>
      )}

      {/* Add/Edit Flight Modal */}
      {showForm && (
        <div className="modal-backdrop">
          <div className="modal">
            <div className="modal-header">
              <h3 className="modal-title">
                {editingFlight ? 'Edit flight' : 'Add new flight'}
              </h3>
              <button
                onClick={() => {
                  setShowForm(false);
                  setEditingFlight(null);
                }}
                className="icon-button"
              >
                <X size={20} />
              </button>
            </div>

            <div className="modal-body">
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <label className="form-label">Origin</label>
                  <AirportSearch
                    value={formData.origin?.code || ''}
                    onChange={(value) => {
                      // Clear origin if user is typing a new search
                      if (value !== formData.origin?.code) {
                        setFormData({ ...formData, origin: null });
                      }
                    }}
                    onSelect={(airport) => setFormData({ ...formData, origin: airport })}
                    placeholder="Search airport..."
                  />
                  {formData.origin && (
                    <div style={{ marginTop: '8px', fontSize: '13px', color: '#666' }}>
                      Selected: {formData.origin.city}, {formData.origin.country} ({formData.origin.code})
                    </div>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">Destination</label>
                  <AirportSearch
                    value={formData.destination?.code || ''}
                    onChange={(value) => {
                      // Clear destination if user is typing a new search
                      if (value !== formData.destination?.code) {
                        setFormData({ ...formData, destination: null });
                      }
                    }}
                    onSelect={(airport) => setFormData({ ...formData, destination: airport })}
                    placeholder="Search airport..."
                  />
                  {formData.destination && (
                    <div style={{ marginTop: '8px', fontSize: '13px', color: '#666' }}>
                      Selected: {formData.destination.city}, {formData.destination.country} ({formData.destination.code})
                    </div>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">Date</label>
                  <input
                    type="date"
                    value={formData.date}
                    onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                    className="input-field"
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Class</label>
                  <select
                    value={formData.serviceClass}
                    onChange={(e) => setFormData({ ...formData, serviceClass: e.target.value })}
                    className="input-field"
                  >
                    {serviceClasses.map(cls => (
                      <option key={cls} value={cls}>{cls}</option>
                    ))}
                  </select>
                </div>

                <button type="submit" className="primary-button" style={{ width: '100%', justifyContent: 'center' }}>
                  {editingFlight ? 'Update flight' : 'Add flight'}
                </button>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FlightTracker;
