import React, { useState, useEffect } from 'react';
import { Plane, Plus, Trash2, Edit2, X, Calendar, LogOut, User, Mail, Lock, Info } from 'lucide-react';

const AIRPORTS_DATABASE = [
  { code: 'JFK', name: 'John F. Kennedy International Airport', city: 'New York', country: 'United States', lat: 40.6413, lon: -73.7781 },
  { code: 'LAX', name: 'Los Angeles International Airport', city: 'Los Angeles', country: 'United States', lat: 33.9416, lon: -118.4085 },
  { code: 'MEX', name: 'Mexico City International Airport', city: 'Mexico City', country: 'Mexico', lat: 19.4361, lon: -99.0719 },
  { code: 'LHR', name: 'London Heathrow Airport', city: 'London', country: 'United Kingdom', lat: 51.4700, lon: -0.4543 },
  { code: 'SIN', name: 'Singapore Changi Airport', city: 'Singapore', country: 'Singapore', lat: 1.3644, lon: 103.9915 },
];

const serviceClasses = ['Economy', 'Premium Economy', 'Business', 'First'];

const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 3958.8;
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

const FlightTracker = () => {
  const [user, setUser] = useState(null);
  const [showAuth, setShowAuth] = useState(true);
  const [authMode, setAuthMode] = useState('login');
  const [flights, setFlights] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingFlight, setEditingFlight] = useState(null);

  // Form state
  const [originSearch, setOriginSearch] = useState('');
  const [destSearch, setDestSearch] = useState('');
  const [flightDate, setFlightDate] = useState('');
  const [flightClass, setFlightClass] = useState('Economy');
  const [flightNumber, setFlightNumber] = useState('');
  const [aircraftType, setAircraftType] = useState('');

  const [showOriginDropdown, setShowOriginDropdown] = useState(false);
  const [showDestDropdown, setShowDestDropdown] = useState(false);
  const [authData, setAuthData] = useState({ email: '', password: '', name: '' });
  const [isVerifying, setIsVerifying] = useState(false);

  // Initialize: Check for active session
  useEffect(() => {
    const activeUser = localStorage.getItem('active-session');
    if (activeUser) {
      const userData = JSON.parse(activeUser);
      setUser(userData);
      setShowAuth(false);
      // Load this specific user's flights
      const allFlights = JSON.parse(localStorage.getItem('user-flights-db') || '{}');
      setFlights(allFlights[userData.email] || []);
    }
  }, []);

  const saveUserFlights = (updatedFlights, userEmail) => {
    const allFlights = JSON.parse(localStorage.getItem('user-flights-db') || '{}');
    allFlights[userEmail] = updatedFlights;
    localStorage.setItem('user-flights-db', JSON.stringify(allFlights));
  };

  const handleAuth = (e) => {
    e.preventDefault();
    const userDb = JSON.parse(localStorage.getItem('app-users-db') || '[]');

    if (authMode === 'register') {
      if (userDb.find(u => u.email === authData.email)) {
        alert('User already exists');
        return;
      }
      const newUser = { ...authData, id: Date.now() };
      userDb.push(newUser);
      localStorage.setItem('app-users-db', JSON.stringify(userDb));
      loginUser(newUser);
    } else {
      const foundUser = userDb.find(u => u.email === authData.email && u.password === authData.password);
      if (foundUser) {
        loginUser(foundUser);
      } else {
        alert('Invalid credentials');
      }
    }
  };

  const loginUser = (userData) => {
    setUser(userData);
    localStorage.setItem('active-session', JSON.stringify(userData));
    const allFlights = JSON.parse(localStorage.getItem('user-flights-db') || '{}');
    setFlights(allFlights[userData.email] || []);
    setShowAuth(false);
    setAuthData({ email: '', password: '', name: '' });
  };

  const handleLogout = () => {
    localStorage.removeItem('active-session');
    setUser(null);
    setShowAuth(true);
    setFlights([]);
  };

  const fetchAirportData = async (code) => {
    const cleanCode = code.trim().toUpperCase();
    const local = AIRPORTS_DATABASE.find(a => a.code === cleanCode);
    if (local) return local;

    try {
      const type = cleanCode.length === 3 ? 'iata' : 'icao';
      const res = await fetch(`https://ryanburnette.github.io/airports-api/${type}/${cleanCode.toLowerCase()}.json`);
      if (res.ok) {
        const data = await res.json();
        return {
          code: cleanCode,
          name: data.airport_name,
          city: data.city,
          lat: data.latitude,
          lon: data.longitude
        };
      }
    } catch (e) { console.error("API error", e); }
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsVerifying(true);

    const originInfo = await fetchAirportData(originSearch);
    const destInfo = await fetchAirportData(destSearch);

    if (!originInfo || !destInfo) {
      alert(`Invalid codes. Please check ${!originInfo ? originSearch : destSearch}`);
      setIsVerifying(false);
      return;
    }

    const dist = Math.round(calculateDistance(originInfo.lat, originInfo.lon, destInfo.lat, destInfo.lon));

    const newFlight = {
      id: editingFlight ? editingFlight.id : Date.now(),
      origin: originInfo.code,
      destination: destInfo.code,
      originCity: originInfo.city,
      destCity: destInfo.city,
      date: flightDate,
      flightNumber,
      aircraftType,
      serviceClass: flightClass,
      distance: dist
    };

    const updated = editingFlight 
      ? flights.map(f => f.id === editingFlight.id ? newFlight : f)
      : [newFlight, ...flights];

    setFlights(updated);
    saveUserFlights(updated, user.email);
    setShowForm(false);
    resetForm();
    setIsVerifying(false);
  };

  const resetForm = () => {
    setEditingFlight(null);
    setOriginSearch('');
    setDestSearch('');
    setFlightDate('');
    setFlightNumber('');
    setAircraftType('');
    setFlightClass('Economy');
  };

  const handleEdit = (flight) => {
    setEditingFlight(flight);
    setOriginSearch(flight.origin);
    setDestSearch(flight.destination);
    setFlightDate(flight.date);
    setFlightNumber(flight.flightNumber || '');
    setAircraftType(flight.aircraftType || '');
    setFlightClass(flight.serviceClass);
    setShowForm(true);
  };

  const handleDelete = (id) => {
    const updated = flights.filter(f => f.id !== id);
    setFlights(updated);
    saveUserFlights(updated, user.email);
  };

  if (showAuth) {
    return (
      <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f5f5f5', padding: '20px' }}>
        <div style={{ background: '#fff', padding: '40px', borderRadius: '16px', width: '100%', maxWidth: '400px', boxShadow: '0 4px 20px rgba(0,0,0,0.08)' }}>
          <h2 style={{ marginBottom: '24px', textAlign: 'center' }}>{authMode === 'login' ? 'FlightLog Login' : 'Create Account'}</h2>
          <form onSubmit={handleAuth} style={{ display: 'grid', gap: '16px' }}>
            {authMode === 'register' && (
              <input placeholder="Full Name" required value={authData.name} onChange={e => setAuthData({...authData, name: e.target.value})} style={{ padding: '12px', borderRadius: '8px', border: '1px solid #ddd' }} />
            )}
            <input type="email" placeholder="Email" required value={authData.email} onChange={e => setAuthData({...authData, email: e.target.value})} style={{ padding: '12px', borderRadius: '8px', border: '1px solid #ddd' }} />
            <input type="password" placeholder="Password" required value={authData.password} onChange={e => setAuthData({...authData, password: e.target.value})} style={{ padding: '12px', borderRadius: '8px', border: '1px solid #ddd' }} />
            <button type="submit" style={{ padding: '12px', background: '#000', color: '#fff', border: 'none', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer' }}>
              {authMode === 'login' ? 'Sign In' : 'Sign Up'}
            </button>
            <button type="button" onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')} style={{ background: 'none', border: 'none', color: '#666', cursor: 'pointer', textDecoration: 'underline' }}>
              {authMode === 'login' ? "Don't have an account? Register" : "Already have an account? Login"}
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div style={{ fontFamily: 'Inter, sans-serif', maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '40px' }}>
        <div>
          <h1 style={{ margin: 0 }}>FlightLog</h1>
          <span style={{ fontSize: '14px', color: '#666' }}>Welcome, {user.name}</span>
        </div>
        <div style={{ display: 'flex', gap: '12px' }}>
          <button onClick={() => setShowForm(true)} style={{ background: '#000', color: '#fff', padding: '10px 20px', borderRadius: '8px', border: 'none', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px' }}>
            <Plus size={18}/> Add Flight
          </button>
          <button onClick={handleLogout} style={{ background: '#f0f0f0', padding: '10px', borderRadius: '8px', border: 'none', cursor: 'pointer' }}><LogOut size={18}/></button>
        </div>
      </header>

      <section style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '40px' }}>
        <div style={{ padding: '20px', border: '1px solid #eee', borderRadius: '12px', textAlign: 'center' }}>
          <div style={{ fontSize: '12px', color: '#666', marginBottom: '4px' }}>TOTAL MILES</div>
          <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{flights.reduce((s, f) => s + f.distance, 0).toLocaleString()}</div>
        </div>
        <div style={{ padding: '20px', border: '1px solid #eee', borderRadius: '12px', textAlign: 'center' }}>
          <div style={{ fontSize: '12px', color: '#666', marginBottom: '4px' }}>TOTAL FLIGHTS</div>
          <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{flights.length}</div>
        </div>
      </section>

      <div style={{ display: 'grid', gap: '16px' }}>
        {flights.map(f => (
          <div key={f.id} style={{ padding: '24px', border: '1px solid #eee', borderRadius: '16px', position: 'relative' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '12px' }}>
              <div>
                <div style={{ fontSize: '20px', fontWeight: 'bold' }}>{f.origin} â†’ {f.destination}</div>
                <div style={{ fontSize: '12px', color: '#888' }}>{f.originCity} to {f.destCity}</div>
              </div>
              <div style={{ display: 'flex', gap: '12px' }}>
                <button onClick={() => handleEdit(f)} style={{ background: 'none', border: 'none', cursor: 'pointer', color: '#666' }}><Edit2 size={16}/></button>
                <button onClick={() => handleDelete(f.id)} style={{ background: 'none', border: 'none', cursor: 'pointer', color: '#ff4d4f' }}><Trash2 size={16}/></button>
              </div>
            </div>

            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px', fontSize: '13px', color: '#444' }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '4px' }}><Calendar size={14}/> {f.date}</div>
              {f.flightNumber && <div>Flight: <strong>{f.flightNumber}</strong></div>}
              {f.aircraftType && <div>Aircraft: <strong>{f.aircraftType}</strong></div>}
              <div style={{ color: '#007bff', fontWeight: '600' }}>{f.serviceClass}</div>
            </div>

            <div style={{ marginTop: '16px', paddingTop: '12px', borderTop: '1px solid #f9f9f9', fontWeight: 'bold', color: '#000' }}>
              {f.distance.toLocaleString()} miles
            </div>
          </div>
        ))}
      </div>

      {showForm && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.6)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000, padding: '20px' }}>
          <div style={{ background: '#fff', padding: '30px', borderRadius: '20px', width: '100%', maxWidth: '500px', maxHeight: '90vh', overflowY: 'auto' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '24px' }}>
              <h2 style={{ margin: 0 }}>{editingFlight ? 'Edit Flight' : 'Add Flight'}</h2>
              <button onClick={() => setShowForm(false)} style={{ background: 'none', border: 'none', cursor: 'pointer' }}><X/></button>
            </div>
            <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '20px' }}>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px' }}>
                <div>
                  <label style={{ fontSize: '12px', fontWeight: 'bold' }}>ORIGIN (IATA/ICAO)</label>
                  <input required value={originSearch} onChange={e => setOriginSearch(e.target.value.toUpperCase())} style={{ width: '100%', padding: '10px', marginTop: '4px', border: '1px solid #ddd', borderRadius: '8px' }} />
                </div>
                <div>
                  <label style={{ fontSize: '12px', fontWeight: 'bold' }}>DESTINATION</label>
                  <input required value={destSearch} onChange={e => setDestSearch(e.target.value.toUpperCase())} style={{ width: '100%', padding: '10px', marginTop: '4px', border: '1px solid #ddd', borderRadius: '8px' }} />
                </div>
              </div>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px' }}>
                <div>
                  <label style={{ fontSize: '12px', fontWeight: 'bold' }}>FLIGHT # (OPTIONAL)</label>
                  <input value={flightNumber} onChange={e => setFlightNumber(e.target.value.toUpperCase())} style={{ width: '100%', padding: '10px', marginTop: '4px', border: '1px solid #ddd', borderRadius: '8px' }} />
                </div>
                <div>
                  <label style={{ fontSize: '12px', fontWeight: 'bold' }}>AIRCRAFT (OPTIONAL)</label>
                  <input value={aircraftType} onChange={e => setAircraftType(e.target.value)} style={{ width: '100%', padding: '10px', marginTop: '4px', border: '1px solid #ddd', borderRadius: '8px' }} />
                </div>
              </div>
              <div>
                <label style={{ fontSize: '12px', fontWeight: 'bold' }}>DATE</label>
                <input type="date" required value={flightDate} onChange={e => setFlightDate(e.target.value)} style={{ width: '100%', padding: '10px', marginTop: '4px', border: '1px solid #ddd', borderRadius: '8px' }} />
              </div>
              <div>
                <label style={{ fontSize: '12px', fontWeight: 'bold' }}>CABIN CLASS</label>
                <select value={flightClass} onChange={e => setFlightClass(e.target.value)} style={{ width: '100%', padding: '10px', marginTop: '4px', border: '1px solid #ddd', borderRadius: '8px' }}>
                  {serviceClasses.map(c => <option key={c} value={c}>{c}</option>)}
                </select>
              </div>
              <button type="submit" disabled={isVerifying} style={{ padding: '14px', background: '#000', color: '#fff', border: 'none', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer' }}>
                {isVerifying ? 'Searching Database...' : 'Save Flight Log'}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default FlightTracker;
