import React, { useState, useEffect } from 'react';
import { Plane, Plus, Trash2, Edit2, X, Calendar, LogOut, User, Mail, Lock, Eye, EyeOff } from 'lucide-react';

// Local database for quick suggestions
const AIRPORTS_DATABASE = [
  { code: 'JFK', name: 'John F. Kennedy International Airport', city: 'New York', country: 'United States', lat: 40.6413, lon: -73.7781 },
  { code: 'LAX', name: 'Los Angeles International Airport', city: 'Los Angeles', country: 'United States', lat: 33.9416, lon: -118.4085 },
  { code: 'LHR', name: 'London Heathrow Airport', city: 'London', country: 'United Kingdom', lat: 51.4700, lon: -0.4543 },
  { code: 'CDG', name: 'Charles de Gaulle Airport', city: 'Paris', country: 'France', lat: 49.0097, lon: 2.5479 },
  { code: 'SIN', name: 'Singapore Changi Airport', city: 'Singapore', country: 'Singapore', lat: 1.3644, lon: 103.9915 },
  { code: 'DXB', name: 'Dubai International Airport', city: 'Dubai', country: 'UAE', lat: 25.2532, lon: 55.3657 },
];

const serviceClasses = ['Economy', 'Premium Economy', 'Business', 'First'];

const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 3958.8; // Miles
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

const FlightTracker = () => {
  const [user, setUser] = useState(null);
  const [showAuth, setShowAuth] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [flights, setFlights] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingFlight, setEditingFlight] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const [originAirport, setOriginAirport] = useState(null);
  const [destAirport, setDestAirport] = useState(null);
  const [flightDate, setFlightDate] = useState('');
  const [flightClass, setFlightClass] = useState('Economy');

  const [originSearch, setOriginSearch] = useState('');
  const [destSearch, setDestSearch] = useState('');
  const [showOriginDropdown, setShowOriginDropdown] = useState(false);
  const [showDestDropdown, setShowDestDropdown] = useState(false);

  const [authData, setAuthData] = useState({ email: '', password: '', name: '' });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userData = localStorage.getItem('user-profile');
    if (userData) { setUser(JSON.parse(userData)); setShowAuth(false); }
    else { setShowAuth(true); }
    const flightsData = localStorage.getItem('flights-data');
    if (flightsData) { setFlights(JSON.parse(flightsData)); }
    setLoading(false);
  }, []);

  const saveFlights = (updatedFlights) => {
    localStorage.setItem('flights-data', JSON.stringify(updatedFlights));
  };

  const fetchOnlineAirport = async (code) => {
    const cleanCode = code.trim().toLowerCase();
    if (cleanCode.length !== 3 && cleanCode.length !== 4) return null;

    // Check local DB first to save a network request
    const localMatch = AIRPORTS_DATABASE.find(a => a.code.toLowerCase() === cleanCode);
    if (localMatch) return localMatch;

    try {
      const type = cleanCode.length === 3 ? 'iata' : 'icao';
      const response = await fetch(`https://ryanburnette.github.io/airports-api/${type}/${cleanCode}.json`);
      if (response.ok) {
        const data = await response.json();
        return {
          code: (data.iata || data.icao).toUpperCase(),
          name: data.airport_name,
          city: data.city,
          country: data.country,
          lat: data.latitude,
          lon: data.longitude
        };
      }
    } catch (err) {
      console.error("Online lookup failed:", err);
    }
    return null;
  };

  const getFilteredAirports = (searchTerm) => {
    if (!searchTerm || searchTerm.length < 2) return [];
    const term = searchTerm.toLowerCase();
    return AIRPORTS_DATABASE.filter(airport =>
      airport.code.toLowerCase().includes(term) ||
      airport.city.toLowerCase().includes(term) ||
      airport.name.toLowerCase().includes(term)
    ).slice(0, 5);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Attempt to resolve codes if the user didn't click the dropdown
    let finalOrigin = originAirport;
    let finalDest = destAirport;

    if (!finalOrigin) {
      finalOrigin = await fetchOnlineAirport(originSearch);
    }
    if (!finalDest) {
      finalDest = await fetchOnlineAirport(destSearch);
    }

    if (!finalOrigin || !finalDest) {
      alert('Could not find those airport codes. Please check the IATA (3-letter) or ICAO (4-letter) codes.');
      setIsSubmitting(false);
      return;
    }

    const distanceMiles = Math.round(calculateDistance(
      finalOrigin.lat, finalOrigin.lon,
      finalDest.lat, finalDest.lon
    ));

    const newFlight = {
      id: editingFlight ? editingFlight.id : Date.now(),
      origin: finalOrigin.code,
      destination: finalDest.code,
      date: flightDate,
      serviceClass: flightClass,
      distanceMiles: distanceMiles,
      distanceKm: Math.round(distanceMiles * 1.60934),
      originAirport: finalOrigin,
      destAirport: finalDest,
      timestamp: editingFlight ? editingFlight.timestamp : Date.now(),
    };

    const updatedFlights = editingFlight
      ? flights.map(f => f.id === editingFlight.id ? newFlight : f)
      : [...flights, newFlight];

    setFlights(updatedFlights);
    saveFlights(updatedFlights);
    setShowForm(false);
    setEditingFlight(null);
    setOriginAirport(null);
    setDestAirport(null);
    setOriginSearch('');
    setDestSearch('');
    setIsSubmitting(false);
  };

  // ... [Other helper functions like handleEdit remain the same] ...
  const handleEdit = (flight) => {
    setEditingFlight(flight);
    setOriginAirport(flight.originAirport);
    setDestAirport(flight.destAirport);
    setOriginSearch(flight.origin);
    setDestSearch(flight.destination);
    setFlightDate(flight.date);
    setFlightClass(flight.serviceClass);
    setShowForm(true);
  };

  if (loading) return <div style={{ textAlign: 'center', marginTop: '20%' }}>Loading...</div>;

  return (
    <div style={{ fontFamily: 'Inter, sans-serif', padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '40px' }}>
        <h1>FlightLog</h1>
        {!showAuth && (
          <button onClick={() => setShowForm(true)} style={{ padding: '10px 20px', background: '#0a0a0a', color: '#fff', border: 'none', borderRadius: '8px', cursor: 'pointer' }}>
            + Add flight
          </button>
        )}
      </header>

      {/* Stats Section */}
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '40px' }}>
        <div style={{ padding: '20px', border: '1px solid #eee', borderRadius: '12px' }}>
          <h3>Total Miles</h3>
          <p style={{ fontSize: '24px', fontWeight: 'bold' }}>{flights.reduce((sum, f) => sum + f.distanceMiles, 0).toLocaleString()}</p>
        </div>
        <div style={{ padding: '20px', border: '1px solid #eee', borderRadius: '12px' }}>
          <h3>Total Flights</h3>
          <p style={{ fontSize: '24px', fontWeight: 'bold' }}>{flights.length}</p>
        </div>
      </div>

      {/* Flights List */}
      {flights.sort((a,b) => new Date(b.date) - new Date(a.date)).map(flight => (
        <div key={flight.id} style={{ padding: '20px', border: '1px solid #eee', borderRadius: '12px', marginBottom: '15px' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <strong>{flight.origin} → {flight.destination}</strong>
            <span>{flight.distanceMiles} miles</span>
          </div>
          <div style={{ color: '#666', fontSize: '14px' }}>{flight.date} • {flight.serviceClass}</div>
          <button onClick={() => handleEdit(flight)} style={{ marginTop: '10px', marginRight: '10px', background: 'none', border: 'none', color: 'blue', cursor: 'pointer' }}>Edit</button>
        </div>
      ))}

      {/* Modal Form */}
      {showForm && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
          <div style={{ background: '#fff', padding: '30px', borderRadius: '16px', width: '100%', maxWidth: '400px' }}>
            <h3>{editingFlight ? 'Edit Flight' : 'Add Flight'}</h3>
            <form onSubmit={handleSubmit}>
              <div style={{ marginBottom: '15px' }}>
                <label>Origin (IATA/ICAO)</label>
                <input 
                  type="text" 
                  value={originSearch} 
                  onChange={(e) => { setOriginSearch(e.target.value.toUpperCase()); setOriginAirport(null); }} 
                  placeholder="e.g. MEX or MMMX" 
                  style={{ width: '100%', padding: '10px', marginTop: '5px' }} 
                  required 
                />
              </div>
              <div style={{ marginBottom: '15px' }}>
                <label>Destination (IATA/ICAO)</label>
                <input 
                  type="text" 
                  value={destSearch} 
                  onChange={(e) => { setDestSearch(e.target.value.toUpperCase()); setDestAirport(null); }} 
                  placeholder="e.g. JFK" 
                  style={{ width: '100%', padding: '10px', marginTop: '5px' }} 
                  required 
                />
              </div>
              <div style={{ marginBottom: '15px' }}>
                <label>Date</label>
                <input type="date" value={flightDate} onChange={(e) => setFlightDate(e.target.value)} style={{ width: '100%', padding: '10px', marginTop: '5px' }} required />
              </div>
              <button 
                type="submit" 
                disabled={isSubmitting}
                style={{ width: '100%', padding: '12px', background: '#0a0a0a', color: '#fff', border: 'none', borderRadius: '8px', cursor: 'pointer' }}
              >
                {isSubmitting ? 'Verifying Codes...' : 'Save Flight'}
              </button>
              <button type="button" onClick={() => setShowForm(false)} style={{ width: '100%', marginTop: '10px', background: 'none', border: 'none', cursor: 'pointer' }}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default FlightTracker;
