import React, { useState, useEffect } from 'react';
import { Plus, Trash2, Edit2, X, Calendar, LogOut, BarChart3, Globe, MapPin, Trophy, Loader2 } from 'lucide-react';

const LOCAL_AIRPORTS = [
  { code: 'JFK', city: 'New York', lat: 40.6413, lon: -73.7781 },
  { code: 'IST', city: 'Istanbul', lat: 41.2753, lon: 28.7519 },
  { code: 'LAX', city: 'Los Angeles', lat: 33.9416, lon: -118.4085 }
];

const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 3958.8;
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return Math.round(R * c);
};

const FlightTracker = () => {
  const [user, setUser] = useState(null);
  const [flights, setFlights] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [isVerifying, setIsVerifying] = useState(false);
  const [formData, setFormData] = useState({
    origin: '', destination: '', date: '', flightNumber: '', aircraftType: '', serviceClass: 'Economy'
  });

  useEffect(() => {
    const session = localStorage.getItem('flightlog-session');
    if (session) {
      const userData = JSON.parse(session);
      setUser(userData);
      const db = JSON.parse(localStorage.getItem('flightlog-db') || '{}');
      setFlights(db[userData.email] || []);
    }
  }, []);

  const saveFlights = (updated) => {
    setFlights(updated);
    const db = JSON.parse(localStorage.getItem('flightlog-db') || '{}');
    db[user.email] = updated;
    localStorage.setItem('flightlog-db', JSON.stringify(db));
  };

  const getAirport = async (code) => {
    const c = code.trim().toUpperCase();
    const local = LOCAL_AIRPORTS.find(a => a.code === c);
    if (local) return local;

    try {
      const response = await fetch(`https://raw.githubusercontent.com/jpatokal/openflights/master/data/airports.dat`);
      const text = await response.text();
      const rows = text.split('\n');
      for (let row of rows) {
        if (row.includes(`"${c}"`)) {
          const parts = row.split(',');
          const lat = parseFloat(parts[parts.length - 8]);
          const lon = parseFloat(parts[parts.length - 7]);
          const city = parts[2].replace(/"/g, '');
          if (!isNaN(lat) && !isNaN(lon)) return { code: c, city, lat, lon };
        }
      }
    } catch (e) { console.error(e); }
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsVerifying(true);
    const from = await getAirport(formData.origin);
    const to = await getAirport(formData.destination);

    if (!from || !to) {
      alert(`Could not find ${!from ? formData.origin : formData.destination}`);
      setIsVerifying(false);
      return;
    }

    const dist = calculateDistance(from.lat, from.lon, to.lat, to.lon);
    const newFlight = { ...formData, id: Date.now(), distance: dist, originCity: from.city, destCity: to.city };
    saveFlights([newFlight, ...flights]);
    setShowForm(false);
    setIsVerifying(false);
    setFormData({ origin: '', destination: '', date: '', flightNumber: '', aircraftType: '', serviceClass: 'Economy' });
  };

  // --- Statistics Calculations ---
  const totalMiles = flights.reduce((sum, f) => sum + (f.distance || 0), 0);
  const moonProgress = Math.min((totalMiles / 238855) * 100, 100).toFixed(1);
  const aircraftCounts = flights.reduce((acc, f) => {
    const type = f.aircraftType || 'Not Specified';
    acc[type] = (acc[type] || 0) + 1;
    return acc;
  }, {});
  const topAircraft = Object.entries(aircraftCounts).sort((a,b) => b[1]-a[1]).slice(0, 3);

  if (!user) return <div style={{padding: '50px', textAlign: 'center'}}>Authentication Required.</div>;

  return (
    <div style={{ maxWidth: '900px', margin: '0 auto', padding: '30px 20px', fontFamily: 'Inter, sans-serif' }}>

      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '40px' }}>
        <h1 style={{ margin: 0, fontWeight: '800' }}>FlightLog</h1>
        <button 
          onClick={() => setShowForm(true)} 
          style={{ background: '#000', color: '#fff', padding: '12px 24px', borderRadius: '12px', border: 'none', fontWeight: 'bold', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px' }}
        >
          <Plus size={20}/> Add Flight
        </button>
      </div>

      {/* Stats Dashboard */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))', gap: '20px', marginBottom: '40px' }}>
        <div style={cardStyle}><Globe size={20} /><div style={valStyle}>{totalMiles.toLocaleString()}</div><div style={lblStyle}>Miles</div></div>
        <div style={cardStyle}><Plane size={20} /><div style={valStyle}>{flights.length}</div><div style={lblStyle}>Flights</div></div>
        <div style={cardStyle}><Trophy size={20} /><div style={valStyle}>{moonProgress}%</div><div style={lblStyle}>To the Moon</div></div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px', marginBottom: '40px' }}>
        <div style={chartBox}>
          <h4 style={{marginTop: 0}}><BarChart3 size={18} /> Top Aircraft</h4>
          {topAircraft.map(([type, count]) => (
            <div key={type} style={{marginBottom: '12px'}}>
              <div style={{display: 'flex', justifyContent: 'space-between', fontSize: '12px', marginBottom: '4px'}}><span>{type}</span><span>{count}</span></div>
              <div style={{height: '6px', background: '#eee', borderRadius: '3px'}}><div style={{height: '100%', background: '#000', width: `${(count/flights.length)*100}%`, borderRadius: '3px'}} /></div>
            </div>
          ))}
        </div>
      </div>

      {/* List */}
      <div style={{ display: 'grid', gap: '15px' }}>
        {flights.map(f => (
          <div key={f.id} style={{ border: '1px solid #eee', borderRadius: '16px', padding: '24px', position: 'relative' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
              <div style={{ fontSize: '20px', fontWeight: 'bold' }}>{f.origin} ‚Üí {f.destination}</div>
              <Trash2 size={18} color="#ff4d4f" style={{ cursor: 'pointer' }} onClick={() => saveFlights(flights.filter(x => x.id !== f.id))} />
            </div>
            <div style={{ color: '#666', fontSize: '14px', marginBottom: '15px' }}>{f.originCity} to {f.destCity}</div>
            <div style={{ display: 'flex', gap: '20px', fontSize: '13px', color: '#444' }}>
              <span>üìÖ {f.date}</span>
              {f.flightNumber && <span>‚úàÔ∏è {f.flightNumber}</span>}
              <span>üè¢ {f.aircraftType || 'Other'}</span>
            </div>
            <div style={{ marginTop: '20px', paddingTop: '15px', borderTop: '1px solid #f5f5f5', fontWeight: 'bold', fontSize: '18px' }}>
              {f.distance.toLocaleString()} miles
            </div>
          </div>
        ))}
      </div>

      {/* Modal Form */}
      {showForm && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.4)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
          <div style={{ background: '#fff', padding: '30px', borderRadius: '20px', width: '100%', maxWidth: '450px' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '25px' }}>
              <h2 style={{ margin: 0 }}>Log Flight</h2>
              <X style={{ cursor: 'pointer' }} onClick={() => setShowForm(false)} />
            </div>
            <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '15px' }}>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
                <input placeholder="Origin (IST)" required value={formData.origin} onChange={e => setFormData({...formData, origin: e.target.value.toUpperCase()})} style={inputStyle} />
                <input placeholder="Dest (JFK)" required value={formData.destination} onChange={e => setFormData({...formData, destination: e.target.value.toUpperCase()})} style={inputStyle} />
              </div>
              <input placeholder="Flight Number (Optional)" value={formData.flightNumber} onChange={e => setFormData({...formData, flightNumber: e.target.value.toUpperCase()})} style={inputStyle} />
              <input placeholder="Aircraft Type (Optional)" value={formData.aircraftType} onChange={e => setFormData({...formData, aircraftType: e.target.value})} style={inputStyle} />
              <input type="date" required value={formData.date} onChange={e => setFormData({...formData, date: e.target.value})} style={inputStyle} />
              <button type="submit" disabled={isVerifying} style={{ padding: '14px', background: '#000', color: '#fff', borderRadius: '12px', fontWeight: 'bold', border: 'none', cursor: 'pointer' }}>
                {isVerifying ? 'Searching Database...' : 'Save Flight'}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

const cardStyle = { padding: '20px', border: '1px solid #eee', borderRadius: '16px', textAlign: 'center', background: '#fff' };
const valStyle = { fontSize: '24px', fontWeight: '800', margin: '8px 0 2px' };
const lblStyle = { fontSize: '12px', color: '#888', textTransform: 'uppercase' };
const chartBox = { padding: '24px', background: '#f9f9f9', borderRadius: '16px' };
const inputStyle = { padding: '12px', borderRadius: '8px', border: '1px solid #ddd', fontSize: '14px' };

export default FlightTracker;
