import React, { useState, useEffect } from 'react';
import { Plane, Plus, Trash2, Edit2, X, Calendar, LogOut, BarChart3, Loader2 } from 'lucide-react';

const AIRPORTS_DATABASE = [
  { code: 'JFK', name: 'John F. Kennedy International Airport', city: 'New York', country: 'United States', lat: 40.6413, lon: -73.7781 },
  { code: 'LAX', name: 'Los Angeles International Airport', city: 'Los Angeles', country: 'United States', lat: 33.9416, lon: -118.4085 },
  { code: 'LHR', name: 'London Heathrow Airport', city: 'London', country: 'United Kingdom', lat: 51.4700, lon: -0.4543 },
  { code: 'MEX', name: 'Mexico City International Airport', city: 'Mexico City', country: 'Mexico', lat: 19.4361, lon: -99.0719 },
  { code: 'SIN', name: 'Singapore Changi Airport', city: 'Singapore', country: 'Singapore', lat: 1.3644, lon: 103.9915 },
];

const serviceClasses = ['Economy', 'Premium Economy', 'Business', 'First'];

const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 3958.8;
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

const FlightTracker = () => {
  const [user, setUser] = useState(null);
  const [showAuth, setShowAuth] = useState(true);
  const [authMode, setAuthMode] = useState('login');
  const [flights, setFlights] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingFlight, setEditingFlight] = useState(null);
  const [isVerifying, setIsVerifying] = useState(false);

  // Form State
  const [originSearch, setOriginSearch] = useState('');
  const [destSearch, setDestSearch] = useState('');
  const [flightDate, setFlightDate] = useState('');
  const [flightClass, setFlightClass] = useState('Economy');
  const [flightNumber, setFlightNumber] = useState('');
  const [aircraftType, setAircraftType] = useState('');

  const [authData, setAuthData] = useState({ email: '', password: '', name: '' });

  useEffect(() => {
    const activeUser = localStorage.getItem('active-session');
    if (activeUser) {
      const userData = JSON.parse(activeUser);
      setUser(userData);
      setShowAuth(false);
      const allFlights = JSON.parse(localStorage.getItem('user-flights-db') || '{}');
      setFlights(allFlights[userData.email] || []);
    }
  }, []);

  const saveUserFlights = (updatedFlights, userEmail) => {
    const allFlights = JSON.parse(localStorage.getItem('user-flights-db') || '{}');
    allFlights[userEmail] = updatedFlights;
    localStorage.setItem('user-flights-db', JSON.stringify(allFlights));
  };

  const handleAuth = (e) => {
    e.preventDefault();
    const userDb = JSON.parse(localStorage.getItem('app-users-db') || '[]');
    if (authMode === 'register') {
      if (userDb.find(u => u.email === authData.email)) return alert('User already exists');
      const newUser = { ...authData, id: Date.now() };
      userDb.push(newUser);
      localStorage.setItem('app-users-db', JSON.stringify(userDb));
      loginUser(newUser);
    } else {
      const foundUser = userDb.find(u => u.email === authData.email && u.password === authData.password);
      if (foundUser) loginUser(foundUser);
      else alert('Invalid credentials');
    }
  };

  const loginUser = (userData) => {
    setUser(userData);
    localStorage.setItem('active-session', JSON.stringify(userData));
    const allFlights = JSON.parse(localStorage.getItem('user-flights-db') || '{}');
    setFlights(allFlights[userData.email] || []);
    setShowAuth(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('active-session');
    setUser(null);
    setShowAuth(true);
    setFlights([]);
  };

  const fetchAirportData = async (code) => {
    const cleanCode = code.trim().toUpperCase();
    const local = AIRPORTS_DATABASE.find(a => a.code === cleanCode);
    if (local) return local;
    try {
      const type = cleanCode.length === 3 ? 'iata' : 'icao';
      const res = await fetch(`https://ryanburnette.github.io/airports-api/${type}/${cleanCode.toLowerCase()}.json`);
      if (res.ok) {
        const data = await res.json();
        return { code: cleanCode, name: data.airport_name, city: data.city, lat: data.latitude, lon: data.longitude };
      }
    } catch (e) { console.error("Lookup failed", e); }
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsVerifying(true);
    const originInfo = await fetchAirportData(originSearch);
    const destInfo = await fetchAirportData(destSearch);

    if (!originInfo || !destInfo) {
      alert(`Could not find ${!originInfo ? originSearch : destSearch}. Check the code and try again.`);
      setIsVerifying(false);
      return;
    }

    const dist = Math.round(calculateDistance(originInfo.lat, originInfo.lon, destInfo.lat, destInfo.lon));
    const newFlight = {
      id: editingFlight ? editingFlight.id : Date.now(),
      origin: originInfo.code, destination: destInfo.code,
      originCity: originInfo.city, destCity: destInfo.city,
      date: flightDate, flightNumber, 
      aircraftType: aircraftType || 'Unknown',
      serviceClass: flightClass, distance: dist
    };

    const updated = editingFlight ? flights.map(f => f.id === editingFlight.id ? newFlight : f) : [newFlight, ...flights];
    setFlights(updated);
    saveUserFlights(updated, user.email);
    setShowForm(false);
    resetForm();
    setIsVerifying(false);
  };

  const resetForm = () => {
    setEditingFlight(null); setOriginSearch(''); setDestSearch('');
    setFlightDate(''); setFlightNumber(''); setAircraftType(''); setFlightClass('Economy');
  };

  const aircraftStats = () => {
    const counts = {};
    flights.forEach(f => { counts[f.aircraftType] = (counts[f.aircraftType] || 0) + 1; });
    return Object.entries(counts).sort((a, b) => b[1] - a[1]).slice(0, 5);
  };

  if (showAuth) {
    return (
      <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: '#f5f5f5', padding: '20px' }}>
        <div style={{ background: '#fff', padding: '40px', borderRadius: '16px', width: '100%', maxWidth: '400px', boxShadow: '0 4px 20px rgba(0,0,0,0.08)' }}>
          <h2 style={{ marginBottom: '24px', textAlign: 'center' }}>{authMode === 'login' ? 'FlightLog Login' : 'Create Account'}</h2>
          <form onSubmit={handleAuth} style={{ display: 'grid', gap: '16px' }}>
            {authMode === 'register' && (
              <input placeholder="Full Name" required value={authData.name} onChange={e => setAuthData({...authData, name: e.target.value})} style={{ padding: '12px', borderRadius: '8px', border: '1px solid #ddd' }} />
            )}
            <input type="email" placeholder="Email" required value={authData.email} onChange={e => setAuthData({...authData, email: e.target.value})} style={{ padding: '12px', borderRadius: '8px', border: '1px solid #ddd' }} />
            <input type="password" placeholder="Password" required value={authData.password} onChange={e => setAuthData({...authData, password: e.target.value})} style={{ padding: '12px', borderRadius: '8px', border: '1px solid #ddd' }} />
            <button type="submit" style={{ padding: '12px', background: '#000', color: '#fff', border: 'none', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer' }}>{authMode === 'login' ? 'Sign In' : 'Sign Up'}</button>
            <button type="button" onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')} style={{ background: 'none', border: 'none', color: '#666', cursor: 'pointer', textDecoration: 'underline' }}>
              {authMode === 'login' ? "Register New Account" : "Back to Login"}
            </button>
          </form>
        </div>
      </div>
    );
  }

  const stats = aircraftStats();
  const maxVal = stats.length > 0 ? stats[0][1] : 1;

  return (
    <div style={{ fontFamily: 'Inter, sans-serif', maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <div><h1 style={{ margin: 0 }}>FlightLog</h1><span style={{ fontSize: '14px', color: '#666' }}>{user.name}'s Dashboard</span></div>
        <div style={{ display: 'flex', gap: '10px' }}>
          <button onClick={() => setShowForm(true)} style={{ background: '#000', color: '#fff', padding: '10px 18px', borderRadius: '8px', border: 'none', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px' }}><Plus size={18}/> Log Flight</button>
          <button onClick={handleLogout} style={{ background: '#eee', border: 'none', borderRadius: '8px', padding: '10px', cursor: 'pointer' }}><LogOut size={18}/></button>
        </div>
      </header>

      {/* Statistics Section */}
      <section style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginBottom: '30px' }}>
        <div style={{ padding: '15px', border: '1px solid #eee', borderRadius: '12px', textAlign: 'center' }}>
          <div style={{ fontSize: '12px', color: '#888' }}>MILES</div>
          <div style={{ fontSize: '20px', fontWeight: 'bold' }}>{flights.reduce((s, f) => s + f.distance, 0).toLocaleString()}</div>
        </div>
        <div style={{ padding: '15px', border: '1px solid #eee', borderRadius: '12px', textAlign: 'center' }}>
          <div style={{ fontSize: '12px', color: '#888' }}>FLIGHTS</div>
          <div style={{ fontSize: '20px', fontWeight: 'bold' }}>{flights.length}</div>
        </div>
      </section>

      {flights.length > 0 && (
        <section style={{ padding: '20px', border: '1px solid #eee', borderRadius: '12px', marginBottom: '30px', background: '#fcfcfc' }}>
          <h4 style={{ margin: '0 0 15px 0', display: 'flex', alignItems: 'center', gap: '8px' }}><BarChart3 size={18}/> Fleet Stats</h4>
          {stats.map(([type, count]) => (
            <div key={type} style={{ marginBottom: '10px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '12px', marginBottom: '4px' }}><span>{type}</span><span>{count}</span></div>
              <div style={{ height: '6px', background: '#e0e0e0', borderRadius: '3px' }}><div style={{ height: '100%', background: '#000', width: `${(count/maxVal)*100}%`, borderRadius: '3px' }} /></div>
            </div>
          ))}
        </section>
      )}

      {/* Flight Cards */}
      <div style={{ display: 'grid', gap: '15px' }}>
        {flights.map(f => (
          <div key={f.id} style={{ padding: '20px', border: '1px solid #eee', borderRadius: '12px', position: 'relative', background: '#fff' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold' }}>{f.origin} ‚Üí {f.destination}</div>
              <div style={{ display: 'flex', gap: '10px' }}>
                <Edit2 size={16} style={{ cursor: 'pointer', color: '#888' }} onClick={() => { setEditingFlight(f); setOriginSearch(f.origin); setDestSearch(f.destination); setFlightDate(f.date); setFlightNumber(f.flightNumber); setAircraftType(f.aircraftType); setFlightClass(f.serviceClass); setShowForm(true); }} />
                <Trash2 size={16} style={{ cursor: 'pointer', color: '#ff4d4f' }} onClick={() => { const u = flights.filter(x => x.id !== f.id); setFlights(u); saveUserFlights(u, user.email); }} />
              </div>
            </div>
            <div style={{ fontSize: '13px', color: '#666', marginBottom: '12px' }}>{f.originCity} to {f.destCity}</div>
            <div style={{ display: 'flex', gap: '15px', fontSize: '12px', color: '#444' }}>
              <span>üìÖ {f.date}</span>
              {f.flightNumber && <span>‚úàÔ∏è {f.flightNumber}</span>}
              <span>üè¢ {f.aircraftType}</span>
              <span style={{ color: '#007bff' }}>{f.serviceClass}</span>
            </div>
            {/* Distance moved to bottom to prevent overlap with icons */}
            <div style={{ marginTop: '12px', paddingTop: '10px', borderTop: '1px solid #f5f5f5', fontWeight: 'bold' }}>
              {f.distance.toLocaleString()} miles
            </div>
          </div>
        ))}
      </div>

      {showForm && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
          <div style={{ background: '#fff', padding: '30px', borderRadius: '16px', width: '100%', maxWidth: '450px' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
              <h2 style={{ margin: 0 }}>Log Flight</h2>
              <X style={{ cursor: 'pointer' }} onClick={() => setShowForm(false)} />
            </div>
            <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '15px' }}>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
                <input placeholder="Origin (e.g. IST)" required value={originSearch} onChange={e => setOriginSearch(e.target.value.toUpperCase())} style={{ padding: '10px', borderRadius: '8px', border: '1px solid #ddd' }} />
                <input placeholder="Dest (e.g. JFK)" required value={destSearch} onChange={e => setDestSearch(e.target.value.toUpperCase())} style={{ padding: '10px', borderRadius: '8px', border: '1px solid #ddd' }} />
              </div>
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
                <input placeholder="Flight # (Optional)" value={flightNumber} onChange={e => setFlightNumber(e.target.value.toUpperCase())} style={{ padding: '10px', borderRadius: '8px', border: '1px solid #ddd' }} />
                <input placeholder="Aircraft (Optional)" value={aircraftType} onChange={e => setAircraftType(e.target.value)} style={{ padding: '10px', borderRadius: '8px', border: '1px solid #ddd' }} />
              </div>
              <input type="date" required value={flightDate} onChange={e => setFlightDate(e.target.value)} style={{ padding: '10px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <select value={flightClass} onChange={e => setFlightClass(e.target.value)} style={{ padding: '10px', borderRadius: '8px', border: '1px solid #ddd' }}>
                {serviceClasses.map(c => <option key={c} value={c}>{c}</option>)}
              </select>
              <button type="submit" disabled={isVerifying} style={{ padding: '12px', background: '#000', color: '#fff', border: 'none', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '8px' }}>
                {isVerifying ? <><Loader2 size={16} className="animate-spin"/> Finding Airport...</> : 'Save Flight'}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default FlightTracker;
