import React, { useState, useEffect } from 'react';
import { Plane, Plus, Trash2, Edit2, X, Calendar, LogOut, Info } from 'lucide-react';

const AIRPORTS_DATABASE = [
  { code: 'JFK', name: 'John F. Kennedy International Airport', city: 'New York', country: 'United States', lat: 40.6413, lon: -73.7781 },
  { code: 'LAX', name: 'Los Angeles International Airport', city: 'Los Angeles', country: 'United States', lat: 33.9416, lon: -118.4085 },
  { code: 'ORD', name: "O'Hare International Airport", city: 'Chicago', country: 'United States', lat: 41.9742, lon: -87.9073 },
  { code: 'LHR', name: 'London Heathrow Airport', city: 'London', country: 'United Kingdom', lat: 51.4700, lon: -0.4543 },
  { code: 'MEX', name: 'Mexico City International Airport', city: 'Mexico City', country: 'Mexico', lat: 19.4361, lon: -99.0719 },
  { code: 'NRT', name: 'Narita International Airport', city: 'Tokyo', country: 'Japan', lat: 35.7720, lon: 140.3929 }
];

const serviceClasses = ['Economy', 'Premium Economy', 'Business', 'First'];

const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 3958.8; // Miles
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

const FlightTracker = () => {
  const [user, setUser] = useState({ name: 'Guest' });
  const [flights, setFlights] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingFlight, setEditingFlight] = useState(null);
  const [isVerifying, setIsVerifying] = useState(false);

  // Form State
  const [formData, setFormData] = useState({
    origin: '',
    destination: '',
    date: '',
    flightNumber: '',
    aircraftType: '',
    serviceClass: 'Economy',
    notes: ''
  });

  useEffect(() => {
    const saved = localStorage.getItem('flights-data');
    if (saved) setFlights(JSON.parse(saved));
  }, []);

  const fetchAirportData = async (code) => {
    const cleanCode = code.trim().toUpperCase();
    // 1. Check local DB
    const local = AIRPORTS_DATABASE.find(a => a.code === cleanCode);
    if (local) return local;

    // 2. Try Online (IATA/ICAO)
    try {
      const type = cleanCode.length === 3 ? 'iata' : 'icao';
      const res = await fetch(`https://ryanburnette.github.io/airports-api/${type}/${cleanCode.toLowerCase()}.json`);
      if (res.ok) {
        const data = await res.json();
        return {
          code: cleanCode,
          name: data.airport_name,
          city: data.city,
          lat: data.latitude,
          lon: data.longitude
        };
      }
    } catch (e) { console.error("API error", e); }
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsVerifying(true);

    const originInfo = await fetchAirportData(formData.origin);
    const destInfo = await fetchAirportData(formData.destination);

    if (!originInfo || !destInfo) {
      alert(`Error: Could not find coordinates for ${!originInfo ? formData.origin : formData.destination}. Please use a valid 3-letter IATA or 4-letter ICAO code.`);
      setIsVerifying(false);
      return;
    }

    const dist = Math.round(calculateDistance(originInfo.lat, originInfo.lon, destInfo.lat, destInfo.lon));

    const newFlight = {
      ...formData,
      id: editingFlight ? editingFlight.id : Date.now(),
      distance: dist,
      origin: originInfo.code,
      destination: destInfo.code,
      cityPair: `${originInfo.city} to ${destInfo.city}`
    };

    const updated = editingFlight 
      ? flights.map(f => f.id === editingFlight.id ? newFlight : f)
      : [newFlight, ...flights];

    setFlights(updated);
    localStorage.setItem('flights-data', JSON.stringify(updated));
    setShowForm(false);
    setEditingFlight(null);
    setIsVerifying(false);
    setFormData({ origin: '', destination: '', date: '', flightNumber: '', aircraftType: '', serviceClass: 'Economy', notes: '' });
  };

  return (
    <div style={{ padding: '20px', maxWidth: '900px', margin: '0 auto', fontFamily: 'sans-serif' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>My Flight Log</h1>
        <button onClick={() => setShowForm(true)} style={{ background: '#000', color: '#fff', padding: '10px 20px', borderRadius: '8px', cursor: 'pointer', border: 'none', display: 'flex', alignItems: 'center', gap: '8px' }}>
          <Plus size={20} /> Add Flight
        </button>
      </div>

      <div style={{ display: 'flex', gap: '20px', marginBottom: '30px' }}>
        <div style={{ flex: 1, padding: '20px', border: '1px solid #ddd', borderRadius: '12px' }}>
          <div style={{ color: '#666', fontSize: '12px' }}>TOTAL DISTANCE</div>
          <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{flights.reduce((s, f) => s + f.distance, 0).toLocaleString()} miles</div>
        </div>
        <div style={{ flex: 1, padding: '20px', border: '1px solid #ddd', borderRadius: '12px' }}>
          <div style={{ color: '#666', fontSize: '12px' }}>FLIGHTS</div>
          <div style={{ fontSize: '24px', fontWeight: 'bold' }}>{flights.length}</div>
        </div>
      </div>

      {flights.map(f => (
        <div key={f.id} style={{ border: '1px solid #eee', padding: '20px', borderRadius: '12px', marginBottom: '15px', position: 'relative' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <span style={{ fontWeight: 'bold', fontSize: '18px' }}>{f.origin} â†’ {f.destination}</span>
            <span style={{ color: '#666' }}>{f.distance} mi</span>
          </div>
          <div style={{ fontSize: '14px', color: '#444', marginTop: '5px' }}>
            {f.date} | {f.flightNumber} | {f.aircraftType} | <span style={{ color: '#007bff' }}>{f.serviceClass}</span>
          </div>
          <button onClick={() => { setEditingFlight(f); setFormData(f); setShowForm(true); }} style={{ position: 'absolute', right: '50px', top: '20px', background: 'none', border: 'none', cursor: 'pointer' }}><Edit2 size={16}/></button>
          <button onClick={() => { const u = flights.filter(x => x.id !== f.id); setFlights(u); localStorage.setItem('flights-data', JSON.stringify(u)); }} style={{ position: 'absolute', right: '20px', top: '20px', background: 'none', border: 'none', cursor: 'pointer', color: 'red' }}><Trash2 size={16}/></button>
        </div>
      ))}

      {showForm && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.7)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
          <div style={{ background: '#fff', padding: '30px', borderRadius: '16px', width: '100%', maxWidth: '500px', maxHeight: '90vh', overflowY: 'auto' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
              <h2>{editingFlight ? 'Edit Flight Details' : 'Log New Flight'}</h2>
              <button onClick={() => setShowForm(false)} style={{ background: 'none', border: 'none', cursor: 'pointer' }}><X /></button>
            </div>

            <form onSubmit={handleSubmit} style={{ display: 'grid', gap: '15px' }}>
              <div style={{ display: 'flex', gap: '10px' }}>
                <div style={{ flex: 1 }}>
                  <label style={{ fontSize: '12px', fontWeight: 'bold' }}>ORIGIN (IATA/ICAO)</label>
                  <input required placeholder="e.g. JFK" value={formData.origin} onChange={e => setFormData({...formData, origin: e.target.value.toUpperCase()})} style={{ width: '100%', padding: '10px', marginTop: '5px' }} />
                </div>
                <div style={{ flex: 1 }}>
                  <label style={{ fontSize: '12px', fontWeight: 'bold' }}>DEST (IATA/ICAO)</label>
                  <input required placeholder="e.g. MEX" value={formData.destination} onChange={e => setFormData({...formData, destination: e.target.value.toUpperCase()})} style={{ width: '100%', padding: '10px', marginTop: '5px' }} />
                </div>
              </div>

              <div style={{ display: 'flex', gap: '10px' }}>
                <div style={{ flex: 1 }}>
                  <label style={{ fontSize: '12px', fontWeight: 'bold' }}>FLIGHT #</label>
                  <input placeholder="e.g. AM403" value={formData.flightNumber} onChange={e => setFormData({...formData, flightNumber: e.target.value.toUpperCase()})} style={{ width: '100%', padding: '10px', marginTop: '5px' }} />
                </div>
                <div style={{ flex: 1 }}>
                  <label style={{ fontSize: '12px', fontWeight: 'bold' }}>AIRCRAFT TYPE</label>
                  <input placeholder="e.g. Boeing 787-9" value={formData.aircraftType} onChange={e => setFormData({...formData, aircraftType: e.target.value})} style={{ width: '100%', padding: '10px', marginTop: '5px' }} />
                </div>
              </div>

              <div>
                <label style={{ fontSize: '12px', fontWeight: 'bold' }}>DATE</label>
                <input type="date" required value={formData.date} onChange={e => setFormData({...formData, date: e.target.value})} style={{ width: '100%', padding: '10px', marginTop: '5px' }} />
              </div>

              <div>
                <label style={{ fontSize: '12px', fontWeight: 'bold' }}>CABIN CLASS</label>
                <select value={formData.serviceClass} onChange={e => setFormData({...formData, serviceClass: e.target.value})} style={{ width: '100%', padding: '10px', marginTop: '5px' }}>
                  {serviceClasses.map(c => <option key={c} value={c}>{c}</option>)}
                </select>
              </div>

              <button type="submit" disabled={isVerifying} style={{ background: '#000', color: '#fff', padding: '15px', borderRadius: '8px', border: 'none', fontWeight: 'bold', cursor: 'pointer', marginTop: '10px' }}>
                {isVerifying ? 'Validating Flight Data...' : 'Save Flight to Log'}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default FlightTracker;
