// Haversine formula for distance calculation
const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 3958.8;
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

// Airport search component
const AirportSearch = ({ value, onChange, onSelect, placeholder }) => {
  const [searchTerm, setSearchTerm] = useState(value || '');
  const [showResults, setShowResults] = useState(false);
  const [filteredAirports, setFilteredAirports] = useState([]);

  useEffect(() => {
    if (searchTerm.length >= 2) {
      const results = AIRPORTS_DATABASE.filter(airport =>
        airport.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
        airport.city.toLowerCase().includes(searchTerm.toLowerCase()) ||
        airport.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        airport.country.toLowerCase().includes(searchTerm.toLowerCase())
      ).slice(0, 8);
      setFilteredAirports(results);
      setShowResults(true);
    } else {
      setShowResults(false);
    }
  }, [searchTerm]);

  const handleSelect = (airport) => {
    setSearchTerm(airport.code);
    onSelect(airport);
    setShowResults(false);
  };

  return (
    <div className="relative">
      <div className="relative">
        <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            onChange(e.target.value);
          }}
          onFocus={() => searchTerm.length >= 2 && setShowResults(true)}
          placeholder={placeholder}
          className="form-input w-full pl-10"
        />
      </div>

      {showResults && filteredAirports.length > 0 && (
        <div className="absolute z-50 w-full mt-2 bg-white rounded-xl shadow-xl border border-gray-200 max-h-80 overflow-y-auto">
          {filteredAirports.map((airport) => (
            <button
              key={airport.code}
              type="button"
              onClick={() => handleSelect(airport)}
              className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors border-b border-gray-100 last:border-0"
            >
              <div className="flex items-center justify-between">
                <div>
                  <div className="font-semibold text-gray-900">{airport.code}</div>
                  <div className="text-sm text-gray-600">{airport.city}, {airport.country}</div>
                </div>
                <div className="text-xs text-gray-400">{airport.name}</div>
              </div>
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

