  const [showForm, setShowForm] = useState(false);
  const [editingFlight, setEditingFlight] = useState(null);
  const [formData, setFormData] = useState({
    origin: null,
    destination: null,
    date: '',
    serviceClass: 'Economy',
  });
  const [authData, setAuthData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(true);

  // Load user and flights from storage
  useEffect(() => {
    const loadData = async () => {
      try {
        const userData = localStorage.getItem('user-profile');
        if (userData) {
          setUser(JSON.parse(userData));
          setShowAuth(false);
        } else {
          setShowAuth(true);
        }

        const flightsData = localStorage.getItem('flights-data');
        if (flightsData) {
          setFlights(JSON.parse(flightsData));
        }
      } catch (error) {
        console.log('No existing data');
        setShowAuth(true);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, []);

  const saveFlights = async (updatedFlights) => {
    try {
      localStorage.setItem('flights-data', JSON.stringify(updatedFlights));
    } catch (error) {
      console.error('Failed to save flights:', error);
    }
  };

  const handleAuth = async (e) => {
    e.preventDefault();

    if (authMode === 'register') {
      if (authData.password !== authData.confirmPassword) {
        alert('Passwords do not match');
        return;
      }

      const newUser = {
        id: Date.now(),
        email: authData.email,
        name: authData.name,
        createdAt: new Date().toISOString()
      };

      localStorage.setItem('user-profile', JSON.stringify(newUser));
      setUser(newUser);
      setShowAuth(false);
    } else {
      // Simple login - in production, this would validate credentials
      const userData = localStorage.getItem('user-profile');
      if (userData) {
        const existingUser = JSON.parse(userData);
        if (existingUser.email === authData.email) {
          setUser(existingUser);
          setShowAuth(false);
        } else {
          alert('User not found. Please register.');
        }
      } else {
        alert('No account found. Please register.');
      }
    }

    setAuthData({ email: '', password: '', confirmPassword: '', name: '' });
  };

  const handleLogout = async () => {
    setUser(null);
    setShowAuth(true);
  };

  const calculateFlightDetails = (origin, destination) => {
    if (!origin || !destination) return null;

    const distanceMiles = calculateDistance(
      origin.lat,
      origin.lon,
      destination.lat,
      destination.lon
    );

    return {
      distanceMiles: Math.round(distanceMiles),
      distanceKm: Math.round(distanceMiles * 1.60934),
      originAirport: origin,
      destAirport: destination,
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.origin || !formData.destination) {
      alert('Please select both origin and destination airports');
      return;
    }

    const flightDetails = calculateFlightDetails(formData.origin, formData.destination);
    if (!flightDetails) {
      alert('Unable to calculate flight distance');
      return;
    }

    const newFlight = {
      id: editingFlight ? editingFlight.id : Date.now(),
      origin: formData.origin.code,
      destination: formData.destination.code,
      date: formData.date,
      serviceClass: formData.serviceClass,
      ...flightDetails,
      timestamp: editingFlight ? editingFlight.timestamp : Date.now(),
    };

    const updatedFlights = editingFlight
      ? flights.map(f => f.id === editingFlight.id ? newFlight : f)
      : [...flights, newFlight];

    setFlights(updatedFlights);
    saveFlights(updatedFlights);

    setShowForm(false);
    setEditingFlight(null);
    setFormData({ origin: null, destination: null, date: '', serviceClass: 'Economy' });
  };

  const handleDelete = async (id) => {
    const updatedFlights = flights.filter(f => f.id !== id);
    setFlights(updatedFlights);
    saveFlights(updatedFlights);
  };

