                {flights.length === 0 ? (
                  <div className="text-center py-20 glass-card rounded-2xl">
                    <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-blue-50 to-indigo-50 rounded-full mb-4">
                      <Plane size={40} className="text-blue-600" />
                    </div>
                    <h3 className="text-xl font-semibold text-gray-900 mb-2">No flights yet</h3>
                    <p className="text-gray-500 mb-6">Start tracking your aviation adventures</p>
                    <button
                      onClick={() => setShowForm(true)}
                      className="btn-primary px-6 py-3 rounded-xl text-white inline-flex items-center gap-2"
                    >
                      <Plus size={20} />
                      Add Your First Flight
                    </button>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {flights.sort((a, b) => new Date(b.date) - new Date(a.date)).map((flight) => (
                      <div key={flight.id} className="flight-card rounded-2xl p-6 border border-gray-200">
                        <div className="flex flex-col lg:flex-row gap-6 items-start">
                          <div className="flex-1 w-full">
                            <div className="flex items-center gap-4 mb-4">
                              <div>
                                <div className="text-3xl font-bold text-gray-900">
                                  {flight.origin}
                                </div>
                                <div className="text-sm text-gray-500 font-medium">{flight.originAirport.city}</div>
                              </div>

                              <div className="flex-1 flex items-center justify-center gap-3">
                                <div className="route-line w-full max-w-[140px]"></div>
                                <Plane className="text-gray-400 rotate-90 plane-trail" size={20} />
                              </div>

                              <div className="text-right">
                                <div className="text-3xl font-bold text-gray-900">
                                  {flight.destination}
                                </div>
                                <div className="text-sm text-gray-500 font-medium">{flight.destAirport.city}</div>
                              </div>
                            </div>

                            <div className="flex flex-wrap gap-2 mb-4">
                              <span className="badge px-3 py-1.5 rounded-lg">
                                {flight.distanceMiles.toLocaleString()} mi
                              </span>
                              <span className="badge px-3 py-1.5 rounded-lg">
                                {flight.distanceKm.toLocaleString()} km
                              </span>
                              <span className="badge px-3 py-1.5 rounded-lg">
                                {flight.serviceClass}
                              </span>
                            </div>

                            <div className="flex items-center gap-2 text-gray-500 text-sm font-medium">
                              <Calendar size={16} />
                              {new Date(flight.date).toLocaleDateString('en-US', { 
                                weekday: 'short',
                                year: 'numeric', 
                                month: 'short', 
                                day: 'numeric' 
                              })}
                            </div>
                          </div>

                          <div className="flex lg:flex-col gap-2">
                            <button
                              onClick={() => handleEdit(flight)}
                              className="p-2.5 hover:bg-blue-50 rounded-xl transition-colors"
                              title="Edit flight"
                            >
                              <Edit2 size={18} className="text-blue-600" />
                            </button>
                            <button
                              onClick={() => handleDelete(flight.id)}
                              className="p-2.5 hover:bg-red-50 rounded-xl transition-colors"
                              title="Delete flight"
                            >
                              <Trash2 size={18} className="text-red-600" />
                            </button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </>
        )}

        {/* Add/Edit Flight Modal */}
        {showForm && (
          <div className="modal-backdrop fixed inset-0 bg-gradient-to-br from-slate-900/50 to-blue-900/50 flex items-center justify-center p-4 z-50">
            <div className="modal-content bg-white rounded-2xl max-w-lg w-full p-8 shadow-2xl">
              <div className="flex items-center justify-between mb-6">
                <h3 className="text-2xl font-bold text-gray-900">
                  {editingFlight ? 'Edit Flight' : 'Add New Flight'}
                </h3>
                <button
                  onClick={() => {
                    setShowForm(false);
                    setEditingFlight(null);
                  }}
                  className="p-2 hover:bg-gray-100 rounded-xl transition-colors"
                >
                  <X size={24} className="text-gray-500" />
                </button>
              </div>

              <form onSubmit={handleSubmit} className="space-y-5">
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Origin Airport
                  </label>
                  <AirportSearch
                    value={formData.origin?.code || ''}
                    onChange={() => {}}
                    onSelect={(airport) => setFormData({ ...formData, origin: airport })}
                    placeholder="Search by code, city, or name..."
                  />
                  {formData.origin && (
                    <div className="mt-2 text-sm text-gray-600">
                      Selected: {formData.origin.name} ({formData.origin.code})
                    </div>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Destination Airport
                  </label>
                  <AirportSearch
                    value={formData.destination?.code || ''}
                    onChange={() => {}}
                    onSelect={(airport) => setFormData({ ...formData, destination: airport })}
                    placeholder="Search by code, city, or name..."
                  />
                  {formData.destination && (
                    <div className="mt-2 text-sm text-gray-600">
                      Selected: {formData.destination.name} ({formData.destination.code})
                    </div>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Flight Date
                  </label>
                  <input
                    type="date"
                    value={formData.date}
                    onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                    className="form-input w-full"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Class of Service
                  </label>
                  <select
                    value={formData.serviceClass}
                    onChange={(e) => setFormData({ ...formData, serviceClass: e.target.value })}
                    className="form-input w-full"
                  >
                    {serviceClasses.map(cls => (
                      <option key={cls} value={cls}>{cls}</option>
                    ))}
                  </select>
                </div>

                <button
                  type="submit"
                  className="btn-primary w-full py-3 rounded-xl text-white text-base font-semibold"
                >
                  {editingFlight ? 'Update Flight' : 'Add Flight'}
                </button>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FlightTracker;
