import React, { useState, useEffect } from 'react';
import { Plane, Plus, Trash2, Edit2, X, Calendar, LogOut, BarChart3, MapPin, Globe, Trophy } from 'lucide-react';

// Main App Component
const FlightTracker = () => {
  const [user, setUser] = useState(null);
  const [flights, setFlights] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [isVerifying, setIsVerifying] = useState(false);
  const [formData, setFormData] = useState({
    origin: '', destination: '', date: '', aircraftType: '', serviceClass: 'Economy'
  });

  useEffect(() => {
    const session = localStorage.getItem('flightlog-session');
    if (session) {
      const userData = JSON.parse(session);
      setUser(userData);
      const db = JSON.parse(localStorage.getItem('flightlog-db') || '{}');
      setFlights(db[userData.email] || []);
    }
  }, []);

  // --- Statistics Logic ---
  const stats = {
    totalMiles: flights.reduce((sum, f) => sum + (f.distance || 0), 0),
    totalFlights: flights.length,
    uniqueAirports: new Set(flights.flatMap(f => [f.origin, f.destination])).size,
    moonProgress: (flights.reduce((sum, f) => sum + (f.distance || 0), 0) / 238855 * 100).toFixed(1),

    aircraft: flights.reduce((acc, f) => {
      const type = f.aircraftType || 'Unknown';
      acc[type] = (acc[type] || 0) + 1;
      return acc;
    }, {}),

    cities: flights.reduce((acc, f) => {
      acc[f.originCity] = (acc[f.originCity] || 0) + 1;
      acc[f.destCity] = (acc[f.destCity] || 0) + 1;
      return acc;
    }, {})
  };

  const topAircraft = Object.entries(stats.aircraft).sort((a,b) => b[1]-a[1]).slice(0, 3);
  const topCities = Object.entries(stats.cities).sort((a,b) => b[1]-a[1]).slice(0, 3);

  return (
    <div style={{ maxWidth: '1000px', margin: '0 auto', padding: '20px', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif' }}>

      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1 style={{ fontSize: '24px', fontWeight: '800' }}>My Flight Log</h1>
        <button onClick={() => setShowForm(true)} style={{ background: '#000', color: '#fff', padding: '12px 24px', borderRadius: '10px', border: 'none', fontWeight: '600', cursor: 'pointer' }}>
          + Add Flight
        </button>
      </div>

      {/* --- Statistics Dashboard --- */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px', marginBottom: '40px' }}>
        <div style={statCardStyle}>
          <div style={iconCircle}><Globe size={20} /></div>
          <div style={statValue}>{stats.totalMiles.toLocaleString()}</div>
          <div style={statLabel}>Total Miles</div>
        </div>
        <div style={statCardStyle}>
          <div style={iconCircle}><Plane size={20} /></div>
          <div style={statValue}>{stats.totalFlights}</div>
          <div style={statLabel}>Flights Flown</div>
        </div>
        <div style={statCardStyle}>
          <div style={iconCircle}><MapPin size={20} /></div>
          <div style={statValue}>{stats.uniqueAirports}</div>
          <div style={statLabel}>Airports Visited</div>
        </div>
        <div style={statCardStyle}>
          <div style={iconCircle}><Trophy size={20} /></div>
          <div style={statValue}>{stats.moonProgress}%</div>
          <div style={statLabel}>To the Moon</div>
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '40px' }}>
        {/* Aircraft Stats */}
        <div style={chartBox}>
          <h3 style={chartTitle}><BarChart3 size={18} style={{marginRight:8}}/> Top Aircraft</h3>
          {topAircraft.map(([type, count]) => (
            <div key={type} style={{ marginBottom: '15px' }}>
              <div style={barLabel}><span>{type}</span><span>{count} flights</span></div>
              <div style={barBg}><div style={{ ...barFill, width: `${(count/flights.length)*100}%` }} /></div>
            </div>
          ))}
        </div>

        {/* City Stats */}
        <div style={chartBox}>
          <h3 style={chartTitle}><MapPin size={18} style={{marginRight:8}}/> Frequent Cities</h3>
          {topCities.map(([city, count]) => (
            <div key={city} style={{ display: 'flex', justifyContent: 'space-between', padding: '10px 0', borderBottom: '1px solid #f0f0f0' }}>
              <span style={{fontWeight: '500'}}>{city}</span>
              <span style={{color: '#666'}}>{count} visits</span>
            </div>
          ))}
        </div>
      </div>

      {/* Flight List Rendering (Omitted for brevity, remains same as previous version) */}
    </div>
  );
};

// --- Styles ---
const statCardStyle = {
  background: '#fff', border: '1px solid #eee', padding: '20px', borderRadius: '16px', textAlign: 'center'
};
const iconCircle = {
  width: '40px', height: '40px', background: '#f5f5f5', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '0 auto 12px'
};
const statValue = { fontSize: '24px', fontWeight: '800', marginBottom: '4px' };
const statLabel = { fontSize: '13px', color: '#666', textTransform: 'uppercase', letterSpacing: '0.5px' };
const chartBox = { background: '#fff', border: '1px solid #eee', padding: '24px', borderRadius: '16px' };
const chartTitle = { margin: '0 0 20px 0', fontSize: '16px', display: 'flex', alignItems: 'center' };
const barLabel = { display: 'flex', justifyContent: 'space-between', fontSize: '12px', marginBottom: '6px', fontWeight: '500' };
const barBg = { height: '8px', background: '#f0f0f0', borderRadius: '4px', overflow: 'hidden' };
const barFill = { height: '100%', background: '#000', borderRadius: '4px' };

export default FlightTracker;
