import React, { useState, useEffect } from 'react';
import { Plane, Plus, Trash2, Edit2, X, Calendar, LogOut, User, Mail, Lock, Eye, EyeOff } from 'lucide-react';

// Local Airport database for quick suggestions
const AIRPORTS_DATABASE = [
  { code: 'JFK', name: 'John F. Kennedy International Airport', city: 'New York', country: 'United States', lat: 40.6413, lon: -73.7781 },
  { code: 'LAX', name: 'Los Angeles International Airport', city: 'Los Angeles', country: 'United States', lat: 33.9416, lon: -118.4085 },
  { code: 'ORD', name: "O'Hare International Airport", city: 'Chicago', country: 'United States', lat: 41.9742, lon: -87.9073 },
  { code: 'ATL', name: 'Hartsfield-Jackson Atlanta International Airport', city: 'Atlanta', country: 'United States', lat: 33.6407, lon: -84.4277 },
  { code: 'DFW', name: 'Dallas/Fort Worth International Airport', city: 'Dallas', country: 'United States', lat: 32.8998, lon: -97.0403 },
  { code: 'DEN', name: 'Denver International Airport', city: 'Denver', country: 'United States', lat: 39.8561, lon: -104.6737 },
  { code: 'SFO', name: 'San Francisco International Airport', city: 'San Francisco', country: 'United States', lat: 37.6213, lon: -122.3790 },
  { code: 'SEA', name: 'Seattle-Tacoma International Airport', city: 'Seattle', country: 'United States', lat: 47.4502, lon: -122.3088 },
  { code: 'MIA', name: 'Miami International Airport', city: 'Miami', country: 'United States', lat: 25.7959, lon: -80.2870 },
  { code: 'LHR', name: 'London Heathrow Airport', city: 'London', country: 'United Kingdom', lat: 51.4700, lon: -0.4543 },
  { code: 'CDG', name: 'Charles de Gaulle Airport', city: 'Paris', country: 'France', lat: 49.0097, lon: 2.5479 },
  { code: 'FRA', name: 'Frankfurt Airport', city: 'Frankfurt', country: 'Germany', lat: 50.0379, lon: 8.5622 },
  { code: 'AMS', name: 'Amsterdam Airport Schiphol', city: 'Amsterdam', country: 'Netherlands', lat: 52.3105, lon: 4.7683 },
  { code: 'MAD', name: 'Madrid-Barajas Airport', city: 'Madrid', country: 'Spain', lat: 40.4936, lon: -3.5668 },
  { code: 'BCN', name: 'Barcelona-El Prat Airport', city: 'Barcelona', country: 'Spain', lat: 41.2974, lon: 2.0833 },
  { code: 'FCO', name: 'Leonardo da Vinci-Fiumicino Airport', city: 'Rome', country: 'Italy', lat: 41.8003, lon: 12.2389 },
  { code: 'HND', name: 'Tokyo Haneda Airport', city: 'Tokyo', country: 'Japan', lat: 35.5494, lon: 139.7798 },
  { code: 'NRT', name: 'Narita International Airport', city: 'Tokyo', country: 'Japan', lat: 35.7720, lon: 140.3929 },
  { code: 'SIN', name: 'Singapore Changi Airport', city: 'Singapore', country: 'Singapore', lat: 1.3644, lon: 103.9915 },
  { code: 'ICN', name: 'Incheon International Airport', city: 'Seoul', country: 'South Korea', lat: 37.4602, lon: 126.4407 },
  { code: 'HKG', name: 'Hong Kong International Airport', city: 'Hong Kong', country: 'Hong Kong', lat: 22.3080, lon: 113.9185 },
  { code: 'DXB', name: 'Dubai International Airport', city: 'Dubai', country: 'UAE', lat: 25.2532, lon: 55.3657 },
  { code: 'SYD', name: 'Sydney Kingsford Smith Airport', city: 'Sydney', country: 'Australia', lat: -33.9399, lon: 151.1753 },
  { code: 'MEL', name: 'Melbourne Airport', city: 'Melbourne', country: 'Australia', lat: -37.6690, lon: 144.8410 },
];

const serviceClasses = ['Economy', 'Premium Economy', 'Business', 'First'];

const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 3958.8; // Miles
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

const FlightTracker = () => {
  const [user, setUser] = useState(null);
  const [showAuth, setShowAuth] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [flights, setFlights] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingFlight, setEditingFlight] = useState(null);

  // Updated state to store FULL objects
  const [originAirport, setOriginAirport] = useState(null);
  const [destAirport, setDestAirport] = useState(null);
  const [flightDate, setFlightDate] = useState('');
  const [flightClass, setFlightClass] = useState('Economy');

  const [originSearch, setOriginSearch] = useState('');
  const [destSearch, setDestSearch] = useState('');
  const [showOriginDropdown, setShowOriginDropdown] = useState(false);
  const [showDestDropdown, setShowDestDropdown] = useState(false);

  const [isSearchingOnline, setIsSearchingOnline] = useState(false);
  const [authData, setAuthData] = useState({ email: '', password: '', confirmPassword: '', name: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userData = localStorage.getItem('user-profile');
    if (userData) {
      setUser(JSON.parse(userData));
      setShowAuth(false);
    } else {
      setShowAuth(true);
    }
    const flightsData = localStorage.getItem('flights-data');
    if (flightsData) {
      setFlights(JSON.parse(flightsData));
    }
    setLoading(false);
  }, []);

  const saveFlights = (updatedFlights) => {
    localStorage.setItem('flights-data', JSON.stringify(updatedFlights));
  };

  // Helper to fetch any airport worldwide via IATA or ICAO
  const fetchOnlineAirport = async (code) => {
    if (!code || (code.length !== 3 && code.length !== 4)) return null;
    try {
      const type = code.length === 3 ? 'iata' : 'icao';
      const response = await fetch(`https://ryanburnette.github.io/airports-api/${type}/${code.toLowerCase()}.json`);
      if (response.ok) {
        const data = await response.json();
        return {
          code: (data.iata || data.icao).toUpperCase(),
          name: data.airport_name,
          city: data.city,
          country: data.country,
          lat: data.latitude,
          lon: data.longitude,
          isOnline: true
        };
      }
    } catch (err) {
      console.error("Lookup error:", err);
    }
    return null;
  };

  // Filter local database and optionally trigger online lookup
  const getFilteredAirports = (searchTerm) => {
    if (!searchTerm || searchTerm.length < 2) return [];
    const term = searchTerm.toLowerCase();
    return AIRPORTS_DATABASE.filter(airport =>
      airport.code.toLowerCase().includes(term) ||
      airport.city.toLowerCase().includes(term) ||
      airport.name.toLowerCase().includes(term)
    ).slice(0, 10);
  };

  // Trigger online lookup if local search yields nothing and it looks like a code
  useEffect(() => {
    const timer = setTimeout(async () => {
      if (originSearch.length >= 3 && getFilteredAirports(originSearch).length === 0) {
        setIsSearchingOnline(true);
        const result = await fetchOnlineAirport(originSearch);
        if (result && !originAirport) {
           // Provide option via dropdown if found
        }
        setIsSearchingOnline(false);
      }
    }, 500);
    return () => clearTimeout(timer);
  }, [originSearch]);

  const selectOrigin = (airport) => {
    setOriginAirport(airport);
    setOriginSearch(airport.code);
    setShowOriginDropdown(false);
  };

  const selectDestination = (airport) => {
    setDestAirport(airport);
    setDestSearch(airport.code);
    setShowDestDropdown(false);
  };

  const handleAuth = (e) => {
    e.preventDefault();
    if (authMode === 'register') {
      if (authData.password !== authData.confirmPassword) {
        alert('Passwords do not match');
        return;
      }
      const newUser = { id: Date.now(), email: authData.email, name: authData.name };
      localStorage.setItem('user-profile', JSON.stringify(newUser));
      setUser(newUser);
      setShowAuth(false);
    } else {
      const userData = localStorage.getItem('user-profile');
      if (userData) {
        const existingUser = JSON.parse(userData);
        if (existingUser.email === authData.email) {
          setUser(existingUser);
          setShowAuth(false);
        } else {
          alert('User not found.');
        }
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Attempt final online lookup if user just typed a code without clicking dropdown
    let finalOrigin = originAirport;
    let finalDest = destAirport;

    if (!finalOrigin && (originSearch.length === 3 || originSearch.length === 4)) {
      finalOrigin = await fetchOnlineAirport(originSearch);
    }
    if (!finalDest && (destSearch.length === 3 || destSearch.length === 4)) {
      finalDest = await fetchOnlineAirport(destSearch);
    }

    if (!finalOrigin || !finalDest) {
      alert('Please select valid airports from the dropdown or enter a valid IATA/ICAO code');
      return;
    }

    const distanceMiles = Math.round(calculateDistance(
      finalOrigin.lat, finalOrigin.lon,
      finalDest.lat, finalDest.lon
    ));

    const newFlight = {
      id: editingFlight ? editingFlight.id : Date.now(),
      origin: finalOrigin.code,
      destination: finalDest.code,
      date: flightDate,
      serviceClass: flightClass,
      distanceMiles: distanceMiles,
      distanceKm: Math.round(distanceMiles * 1.60934),
      originAirport: finalOrigin,
      destAirport: finalDest,
      timestamp: editingFlight ? editingFlight.timestamp : Date.now(),
    };

    const updatedFlights = editingFlight
      ? flights.map(f => f.id === editingFlight.id ? newFlight : f)
      : [...flights, newFlight];

    setFlights(updatedFlights);
    saveFlights(updatedFlights);
    setShowForm(false);
    setEditingFlight(null);
    setOriginAirport(null);
    setDestAirport(null);
    setOriginSearch('');
    setDestSearch('');
  };

  const handleEdit = (flight) => {
    setEditingFlight(flight);
    setOriginAirport(flight.originAirport);
    setDestAirport(flight.destAirport);
    setOriginSearch(flight.origin);
    setDestSearch(flight.destination);
    setFlightDate(flight.date);
    setFlightClass(flight.serviceClass);
    setShowForm(true);
  };

  const openAddForm = () => {
    setEditingFlight(null);
    setOriginAirport(null);
    setDestAirport(null);
    setOriginSearch('');
    setDestSearch('');
    setFlightDate('');
    setFlightClass('Economy');
    setShowForm(true);
  };

  const totalMiles = flights.reduce((sum, flight) => sum + flight.distanceMiles, 0);
  const totalKm = flights.reduce((sum, flight) => sum + flight.distanceKm, 0);

  const originResults = getFilteredAirports(originSearch);
  const destResults = getFilteredAirports(destSearch);

  return (
    <div style={{ fontFamily: 'Inter, sans-serif', minHeight: '100vh', background: '#ffffff' }}>
      <style>{`@keyframes spin { to { transform: rotate(360deg); } } body { margin: 0; }`}</style>

      {showAuth && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '24px', zIndex: 1000 }}>
          <div style={{ background: '#fff', borderRadius: '16px', maxWidth: '480px', width: '100%', padding: '32px' }}>
            <h2 style={{ fontSize: '24px', fontWeight: '600', marginBottom: '24px' }}>{authMode === 'login' ? 'Welcome back' : 'Create account'}</h2>
            <form onSubmit={handleAuth} style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
              {authMode === 'register' && (
                <input type="text" placeholder="Name" value={authData.name} onChange={(e) => setAuthData({ ...authData, name: e.target.value })} style={{ width: '100%', padding: '12px', border: '1px solid #e5e5e5', borderRadius: '8px' }} required />
              )}
              <input type="email" placeholder="Email" value={authData.email} onChange={(e) => setAuthData({ ...authData, email: e.target.value })} style={{ width: '100%', padding: '12px', border: '1px solid #e5e5e5', borderRadius: '8px' }} required />
              <input type="password" placeholder="Password" value={authData.password} onChange={(e) => setAuthData({ ...authData, password: e.target.value })} style={{ width: '100%', padding: '12px', border: '1px solid #e5e5e5', borderRadius: '8px' }} required />
              <button type="submit" style={{ padding: '12px', background: '#0a0a0a', color: '#fff', border: 'none', borderRadius: '8px', fontWeight: '600', cursor: 'pointer' }}>{authMode === 'login' ? 'Sign in' : 'Create account'}</button>
              <button type="button" onClick={() => setAuthMode(authMode === 'login' ? 'register' : 'login')} style={{ background: 'none', border: 'none', color: '#666', cursor: 'pointer', textDecoration: 'underline' }}>{authMode === 'login' ? "Need an account?" : 'Have an account?'}</button>
            </form>
          </div>
        </div>
      )}

      {!showAuth && user && (
        <>
          <header style={{ borderBottom: '1px solid #e5e5e5', background: '#fff' }}>
            <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px 32px', display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
              <div style={{ fontSize: '20px', fontWeight: '600' }}>FlightLog</div>
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <span style={{ fontSize: '14px' }}>{user.name}</span>
                <button onClick={() => setUser(null)} style={{ background: 'none', border: 'none', cursor: 'pointer' }}><LogOut size={20} /></button>
                <button onClick={openAddForm} style={{ padding: '10px 20px', background: '#0a0a0a', color: '#fff', border: 'none', borderRadius: '8px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px' }}><Plus size={18} /> Add flight</button>
              </div>
            </div>
          </header>

          <main style={{ maxWidth: '1200px', margin: '0 auto', padding: '48px 32px' }}>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '24px', marginBottom: '48px' }}>
              <div style={{ border: '1px solid #e5e5e5', borderRadius: '12px', padding: '24px' }}>
                <div style={{ fontSize: '13px', color: '#666' }}>TOTAL MILES</div>
                <div style={{ fontSize: '36px', fontWeight: '700' }}>{totalMiles.toLocaleString()}</div>
              </div>
              <div style={{ border: '1px solid #e5e5e5', borderRadius: '12px', padding: '24px' }}>
                <div style={{ fontSize: '13px', color: '#666' }}>TOTAL KM</div>
                <div style={{ fontSize: '36px', fontWeight: '700' }}>{totalKm.toLocaleString()}</div>
              </div>
            </div>

            {flights.sort((a,b) => new Date(b.date) - new Date(a.date)).map((flight) => (
              <div key={flight.id} style={{ border: '1px solid #e5e5e5', borderRadius: '12px', padding: '24px', marginBottom: '16px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <div style={{ fontSize: '24px', fontWeight: '700' }}>{flight.origin}  {flight.destination}</div>
                  <div style={{ display: 'flex', gap: '8px' }}>
                    <button onClick={() => handleEdit(flight)} style={{ background: 'none', border: 'none', cursor: 'pointer' }}><Edit2 size={16} /></button>
                    <button onClick={() => { setFlights(flights.filter(f => f.id !== flight.id)); saveFlights(flights.filter(f => f.id !== flight.id)); }} style={{ background: 'none', border: 'none', cursor: 'pointer' }}><Trash2 size={16} /></button>
                  </div>
                </div>
                <div style={{ color: '#666', fontSize: '14px' }}>{flight.serviceClass}  {new Date(flight.date).toLocaleDateString()}</div>
                <div style={{ marginTop: '8px', fontWeight: '500' }}>{flight.distanceMiles.toLocaleString()} miles / {flight.distanceKm.toLocaleString()} km</div>
              </div>
            ))}
          </main>
        </>
      )}

      {showForm && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '24px', zIndex: 1000 }}>
          <div style={{ background: '#fff', borderRadius: '16px', maxWidth: '480px', width: '100%', padding: '24px' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
              <h3>{editingFlight ? 'Edit Flight' : 'Add Flight'}</h3>
              <button onClick={() => setShowForm(false)} style={{ background: 'none', border: 'none', cursor: 'pointer' }}><X size={20} /></button>
            </div>
            <form onSubmit={handleSubmit}>
              <div style={{ marginBottom: '16px', position: 'relative' }}>
                <label style={{ fontSize: '12px', color: '#666' }}>ORIGIN</label>
                <input type="text" value={originSearch} onChange={(e) => { setOriginSearch(e.target.value); setShowOriginDropdown(true); }} placeholder="e.g. MEX or MMMX" style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '6px' }} />
                {showOriginDropdown && originResults.length > 0 && (
                  <div style={{ position: 'absolute', width: '100%', background: '#fff', border: '1px solid #ddd', zIndex: 10 }}>
                    {originResults.map(a => (
                      <div key={a.code} onClick={() => selectOrigin(a)} style={{ padding: '8px', cursor: 'pointer' }}>{a.code} - {a.city}</div>
                    ))}
                  </div>
                )}
              </div>
              <div style={{ marginBottom: '16px', position: 'relative' }}>
                <label style={{ fontSize: '12px', color: '#666' }}>DESTINATION</label>
                <input type="text" value={destSearch} onChange={(e) => { setDestSearch(e.target.value); setShowDestDropdown(true); }} placeholder="e.g. JFK" style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '6px' }} />
                {showDestDropdown && destResults.length > 0 && (
                  <div style={{ position: 'absolute', width: '100%', background: '#fff', border: '1px solid #ddd', zIndex: 10 }}>
                    {destResults.map(a => (
                      <div key={a.code} onClick={() => selectDestination(a)} style={{ padding: '8px', cursor: 'pointer' }}>{a.code} - {a.city}</div>
                    ))}
                  </div>
                )}
              </div>
              <div style={{ marginBottom: '16px' }}>
                <label style={{ fontSize: '12px', color: '#666' }}>DATE</label>
                <input type="date" value={flightDate} onChange={(e) => setFlightDate(e.target.value)} style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '6px' }} required />
              </div>
              <button type="submit" style={{ width: '100%', padding: '12px', background: '#0a0a0a', color: '#fff', border: 'none', borderRadius: '8px', cursor: 'pointer' }}>Save Flight</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default FlightTracker;
